sp_widget.client_script:
function(spUtil, $scope) {
  var c = this;
	if (!c.data.authenticated)
		processLogin();


	function processLogin() {
		spUtil.get("ama_login")
			.then(function(r) {
				c.loginWidget = r;
		});
	}


}

sp_widget.css:
$grey: $gray;
